/**
 * The arguments for the `onChange` ColorPicker event.
 */
export interface ColorPickerChangeEvent {
    /**
     * The current value of the ColorPicker.
     */
    value: string;
    /**
     * The current value of the ColorPicker in RGBA format.
     */
    rgbaValue: string;
    /**
     * A native DOM event.
     */
    event: any;
}
